// Extract and validate input
const body = $input.first().json.body;

const customerId = body.customer_id || `GUEST_${Date.now()}`;
const customerName = body.customer_name || 'Guest';
const message = body.message;
const conversationId = body.conversation_id || null;
const metadata = {
  platform: body.platform || 'web',
  category: body.category || 'general',
  priority: body.priority || 'medium',
  customerEmail: body.customer_email || null,
  customerContact: body.customer_contact || null
};

if (!message || message.trim() === '') {
  throw new Error('Message is required');
}

console.log('=== INCOMING CHAT REQUEST ===');
console.log('Customer:', customerName, '(' + customerId + ')');
console.log('Message:', message);
console.log('Conversation ID:', conversationId || 'NEW');
console.log('===========================');

return [{
  json: {
    customerId,
    customerName,
    message: message.trim(),
    conversationId,
    metadata,
    timestamp: new Date().toISOString()
  }
}];

// Get original message from earlier node
const inputData = $('Validate & Parse Input').first().json;
const searchResults = $input.first().json.result || [];

let contextText = '';
let sources = [];

if (searchResults.length > 0) {
  contextText = searchResults.map((result, index) => {
    const payload = result.payload;
    const score = result.score;

    // Add to sources for reference
    sources.push({
      productName: payload.productName,
      modelCode: payload.modelCode,
      brandName: payload.brandName,
      categoryName: payload.categoryName,
      title: payload.title,
      score: score
    });

    return `[${index + 1}] ${payload.brandName} ${payload.productName} (${payload.modelCode})
${payload.documentTitle}: ${payload.title}
${payload.pageContent || payload.content}
---`;
  }).join('\n\n');
}

console.log('=== CONTEXT PREPARED ===');
console.log('Found:', searchResults.length, 'relevant documents');
console.log('Context length:', contextText.length, 'characters');
console.log('=======================');

return [{
  json: {
    ...inputData,
    contextText,
    sources,
    hasContext: searchResults.length > 0
  }
}];

