const contextData = $('Prepare Context for AI').first().json;
const aiResponse = $input.first().json;

// Extract AI answer
const answer = aiResponse.candidates?.[0]?.content?.parts?.[0]?.text || 'Sorry, I could not generate a response.';
const tokensUsed = aiResponse.usageMetadata?.totalTokenCount || 0;

console.log('=== AI RESPONSE ===');
console.log('Answer length:', answer.length, 'characters');
console.log('Tokens used:', tokensUsed);
console.log('==================');

return [{
  json: {
    ...contextData,
    aiAnswer: answer,
    tokensUsed,
    aiMetadata: {
      model: 'gemini-2.0-flash-exp',
      totalTokens: tokensUsed,
      finishReason: aiResponse.candidates?.[0]?.finishReason || 'unknown'
    }
  }
}];

const extractedData = $('Extract Answer').first().json;
const userMsgResult = $('Save User Message to PostgreSQL').first().json;
const responseTimeMs = Date.now() - new Date(extractedData.timestamp).getTime();

const response = {
  success: true,
  conversation_id: userMsgResult.conversation_id,
  response: extractedData.aiAnswer,
  metadata: {
    total_messages: (userMsgResult.total_messages || 0) + 2, // +2 for user + AI messages
    tokens_used: extractedData.tokensUsed,
    response_time_ms: responseTimeMs,
    model: extractedData.aiMetadata.model,
    sources_found: extractedData.sources.length,
    has_context: extractedData.hasContext
  },
  sources: extractedData.sources.slice(0, 3) // Top 3 sources
};

console.log('=== FINAL RESPONSE ===');
console.log('Conversation ID:', response.conversation_id);
console.log('Response time:', responseTimeMs, 'ms');
console.log('Tokens:', extractedData.tokensUsed);
console.log('======================');

return [{ json: response }];
